(()=>{"use strict";class t{constructor(t,s,o){this.id=t,this.text=s,this.status=o}}const s=new class{constructor(t,s){this.todos=[],this.inputElement=document.getElementById(t),this.todoListElement=document.getElementById(s),this.loadTodos(),this.renderTodos()}loadTodos(){const t=localStorage.getItem("todos");t&&(this.todos=JSON.parse(t))}saveTodos(){localStorage.setItem("todos",JSON.stringify(this.todos))}renderTodos(){this.todoListElement.innerHTML="",this.todos.forEach((t=>{const s=document.createElement("li");s.textContent=`${t.text} - Статус: ${t.status}`,this.todoListElement.appendChild(s)}))}addTodo(){const s=this.inputElement.value.trim();if(s){const o=new t(this.todos.length+1,s,"план");this.todos.push(o),this.saveTodos(),this.renderTodos(),this.inputElement.value=""}}}("taskInput","taskList");document.getElementById("addButton").addEventListener("click",(()=>{s.addTodo()}))})();